  loadAssessmentYears() {
    let currentYear = new Date().getFullYear();
    this.assessmentYears = [];

    for (var i = 2015; i < currentYear; i++) {
      let assessmentYear = { id: i, year: i };
      this.assessmentYears.push(assessmentYear);
    }
  }

